{"questionId": 2645, "knowledgebaseId": 22, "title": "Un vol ou un bris de glace a-t-il une incidence sur mon coefficient de bonus/malus ?", "titleReword": "calcul bonus, calcul malus", "status": "active", "filters": [{"filterId": 55, "name": "particuliers-55", "type": "tag", "active": "active", "slug": null}, {"filterId": 60, "name": "appli-60", "type": "tag", "active": "active", "slug": null}, {"filterId": 61, "name": "site-61", "type": "tag", "active": "active", "slug": null}, {"filterId": 172, "name": "bonus-malus-172", "type": "thematic", "active": "active", "slug": "bonus-malus"}, {"filterId": 177, "name": "sinistres-177", "type": "thematic", "active": "active", "slug": "sinistres"}], "responses": [{"responseId": 2978, "body": "\"Le coefficient de bonus-malus ne change pas, m\u00eame en cas de :   sinistres vol, incendie, bris de glace,  \u00e9v\u00e8nement climatique,  catastrophe naturelle.\"", "channel": "faq", "status": "published"}], "thematics": [{"thematicId": 172, "name": "bonus-malus-172", "title": "Bonus/malus", "slug": "bonus-malus", "active": "active"}, {"thematicId": 177, "name": "sinistres-177", "title": "Mes sinistres", "slug": "sinistres", "active": "active"}], "tags": [{"tagId": 55, "name": "particuliers-55", "title": "Particuliers", "active": "active"}, {"tagId": 60, "name": "appli-60", "title": "Appli", "active": "active"}, {"tagId": 61, "name": "site-61", "title": "Site", "active": "active"}], "promoted": false, "frequent": false, "boost": 0, "questionSlug": "vol-un-bris-glace-une-incidence-mon-coefficient-bonus-malus"}
