{"questionId": 2552, "knowledgebaseId": 22, "title": "Quels sont les montants minimum et maximum \u00e0 emprunter pour un cr\u00e9dit personnel Macif ?", "titleReword": null, "status": "active", "filters": [{"filterId": 55, "name": "particuliers-55", "type": "tag", "active": "active", "slug": null}, {"filterId": 60, "name": "appli-60", "type": "tag", "active": "active", "slug": null}, {"filterId": 61, "name": "site-61", "type": "tag", "active": "active", "slug": null}, {"filterId": 146, "name": "banque-quotidien-146", "type": "thematic", "active": "active", "slug": "banque-quotidien"}], "responses": [{"responseId": 2885, "body": "\"Le montant minimum \u00e0 emprunter pour un cr\u00e9dit projet Macif est de 1 000 \u20ac et le montant maximum est de 21 500 \u20ac\"", "channel": "faq", "status": "published"}], "thematics": [{"thematicId": 146, "name": "banque-quotidien-146", "title": "Banque au quotidien", "slug": "banque-quotidien", "active": "active"}], "tags": [{"tagId": 55, "name": "particuliers-55", "title": "Particuliers", "active": "active"}, {"tagId": 60, "name": "appli-60", "title": "Appli", "active": "active"}, {"tagId": 61, "name": "site-61", "title": "Site", "active": "active"}], "promoted": false, "frequent": false, "boost": 0, "questionSlug": "sont-montants-minimum-maximum-emprunter-un-credit-personnel-macif"}
