}], "tags": [{"tagId": 55, "name": "particuliers-55", "title": "Particuliers", "active": "active"}, {"tagId": 60, "name": "appli-60", "title": "Appli", "active": "active"}, {"tagId": 61, "name": "site-61", "title": "Site", "active": "active"}], "promoted": false, "frequent": false, "boost": 0, "questionSlug": "si-prefere-dautres-prestations-comment-serai-je-rembourse"}
