{"tagId": 55, "name": "particuliers-55", "title": "Particuliers", "active": "active"}, {"tagId": 56, "name": "professionnels-56", "title": "Professionnels", "active": "active"}, {"tagId": 60, "name": "appli-60", "title": "Appli", "active": "active"}, {"tagId": 61, "name": "site-61", "title": "Site", "active": "active"}], "promoted": false, "frequent": false, "boost": 0, "questionSlug": "finance-reforme-100-sante"}
